import { useState } from 'react';

const useInput = (props) => {
  const [hookName, setHookName] = useState(props.name);
  const [value, setValue] = useState(props.value);
  const [valueActivated, setValueActivated] = useState(false);

  const updateValues = () => {
    hookName = hookName;

    if(hookName == "name") {
      const nameActivated = !valueActivated  || value.trim() != 0 && valueActivated;

      console.log("cccc", nameActivated)
      setValueActivated(nameActivated)
    } else if ( hookName == "email") {
      const emailFormat  = value.split("").find((char) => char=="@")
      const emailIsValid = !valueActivated || value.trim() != 0 && valueActivated && emailFormat;
      setValueActivated(emailIsValid)
    }
  }

  return [value, valueActivated, setValue, setValueActivated, updateValues]
}

export default useInput;
