import { useState } from 'react';

const useInput = (props) => {
  [hookName, setHookName] = useState(props.name);
  [value, setValue] = useState(porps.value);
  [valueActivated, setValueActivated] = useState(false);

  if(hookName == "name") {
    const nameActivated = !valueActivated  || value.trim() != 0 && valueActivated;
    setValueActivated(nameActivated)
  } else if ( hookName == "email") {
    const emailFormat  = value.split("").find((char) => char=="@")
    const emailIsValid = !emailActivated || email.trim() != 0 && emailActivated && emailFormat;
    setValueActivated(emailIsValid)
  }

  return [value, valueActivated]
}

export default useInput;
