import { useRef } from "react";
import useInput from "../hooks/useInput";

const SimpleInput = (props) => {
  const nameRef = useRef();
  let nameHook = useInput("name", "")
  let emailHook = useInput("email", "")

  const nameInput = nameHook;
  const emailInput = emailHook;

  const nameHandler = (event) => {
    nameInput[2](event.target.value)
    nameInput[3](true)
  }
  const onBlurHandler = (event) => {
    nameInput[2](event.target.value)
    nameInput[4]
    // setNameActivated(true);
  }
  const emailHandler = (event) => {
    // setEmail(event.target.value)
    // setEmailActivated(true);
  }

  // console.log(nameInput[0], nameInput[1], nameInput[2])
  const nameInputCLases = nameInput[1] ? "form-control" : "form-control invalid"
  const emailInputCLases = emailInput[1] ? "form-control" : "form-control invalid"

  let formIsValid = false;
  if(nameInput[1] && emailInput[1]) {
    formIsValid = true;
  } else {
    formIsValid = false;
  }

  const formSubmisionHandler = (event) => {
    event.preventDefault();

    if(!formIsValid) {
      return;
    }
    // setName("")
    // setNameActivated(false)
  }

  return (
    <form onSubmit={formSubmisionHandler}>
      <div className={nameInputCLases}>
        <label htmlFor='name'>Your Name</label>
        <input className={nameInputCLases} ref={nameRef} type='text' id='name' value={nameInput[0]} onChange={nameHandler} onBlur={onBlurHandler}/>
        { !nameInput[1] && <p className="error-text"> please check that the name is correct </p>}
      </div>
      <div className={emailInputCLases}>
        <label htmlFor="email"> Email </label>
        <input className="form-control" onChange={emailHandler} />
        { !emailInput[1] && <p className="error-text"> please check that the name is correct </p>}
      </div>
      <div className="form-actions">
        <button disabled={true}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
