import { useState, useRef } from "react";

const SimpleInput = (props) => {
  const nameRef = useRef();
  const [name, setName] = useState("");
  const [nameIsValid, setNameIsValid] = useState(true)

  const nameHandler = (event) => {
    setName(event.target.value)
  }

  const formSubmisionHandler = (event) => {
    event.preventDefault();
    console.log(name)
    console.log(nameRef.current.value)
    if(name.trim() == 0) {
      setNameIsValid(false)
      return;
    }
    setNameIsValid(true);
    setName("")
  }

  const nameInputCLases = nameIsValid ? "form-control" : "form-control david"

  return (
    <form onSubmit={formSubmisionHandler}>
      <div className='form-control'>
        <label htmlFor='name'>Your Name</label>
        <input className="david" ref={nameRef} type='text' id='name' value={name} onChange={nameHandler}/>
        { !nameIsValid && <p className="error-text"> please check that the name is correct </p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
